import * as React from "react";
import { Props as BaseProps } from 'one-ui';

import Col from "antd/lib/col";
import AntCard from "antd/lib/card";
import { CardSize } from 'antd/lib/card';
import { CardTitle } from "./title";
import { useCtx } from "one-ui-provider";

const { Meta } = AntCard;

interface Props extends BaseProps {
  span?: number;
  size?: CardSize,
  vscale?: number,
  notitle?: boolean,
  title?: JSX.Element;
  titleIcon?: string;
  titleLabel?: string;
  titleStyle?: string;
  titleIconWidth?: string,
  titleIconHeight?: string,
  titleIconResolve?: boolean,
  headerActions?: JSX.Element[];
  footerActions?: JSX.Element[];
  cover?: JSX.Element;
}

export const Card = (props: Props) => {
  const {
    style,
    children,
    className,
    span = 8,
    size = 'small',
    vscale = 1,
    notitle = false,
    title,
    titleIcon,
    titleLabel,
    titleStyle,
    titleIconWidth,
    titleIconHeight = '32px',
    titleIconResolve = true,
    headerActions,
    footerActions,
    cover,
  } = props;

  const { resolver } = useCtx();
  const titleIconUrl = titleIcon && ((titleIconResolve) ?
    resolver(titleIcon) : titleIcon);

  let elemTitle;
  if (!(notitle)) {
    elemTitle = title || (
      <CardTitle
        icon={titleIconUrl}
        title={titleLabel}
        style={titleStyle}
        width={titleIconWidth}
        height={titleIconHeight}
      />
    );
  }

  return (
    <Col span={span}>
      <AntCard
        size={size}
        style={style}
        className={className || `ov-card ov-card-${vscale}`}
        extra={headerActions}
        actions={footerActions}
        cover={cover}
      >
        <Meta
          title={elemTitle}
          description={
            <div className="ov-card-content">
              {children}
            </div>
          }
        />
      </AntCard>
    </Col >
  );
};
