    if(referal && (referal.length == 1)) {
        resolverui =(
                        <div style ={ {
                            'textAlign':"center",
                            'padding': '36px 32px'
                        }}>
                        <img src={resolver(referalicon)} width={"30%"} height={"30%"} />
                        <p style={{ marginBottom: '4px', padding: '0px', fontSize: '.85rem' }}  >

                        <strong style={{ margin: '0px', padding: '0px', fontSize: '3rem', alignItems: 'center'}}>
