                        <strong style={{ margin: '0px', padding: '0px', fontSize: '3rem' }}>
                            {referal.length} Referrals
                        </strong>
                        </p>
                        </div>
                    )
    }
    else
    {
        if(referal && (referal.length == 0))
           {
               resolverui = (
                <div style={{ textAlign: "left", lineHeight: "22px", alignItems: 'center' }}>
                <Result style={{padding: '24px 32px'}}
              status="warning"
