import * as React from "react";
import Descriptions from "antd/lib/descriptions";
import Divider from "antd/lib/divider";
import Icon from 'antd/lib/icon';
import Row from "antd/lib/row";
import Col from "antd/lib/col";
import Select from "antd/lib/select";
import Typography from "antd/lib/typography";
import { useDispatch } from "react-redux";
import { useSelector } from 'react-redux';
import { ec } from 'one-model-ec';
import { address } from 'one-model-address';
import { array } from 'one-core';
import { useState } from 'react';
import { NV } from 'one-micro';
const { Option } = Select;
const { Text } = Typography;


export const Vitals = (props:any) => {

  const dispatch = useDispatch();
  const { patientdetails, selected: patientvalue = {} } = useSelector((state: any) => (
    state.patient || {}
  ));
  const pcpdata = useSelector((state:any) => (state.pcp || []));
  const [contactIndex, setContactIndex] = useState(0);


  const econtacts = patientvalue.EmergencyContacts || [];


  let backspace = (
    <Select style={{ width: 180 }} value={econtacts && econtacts[contactIndex] && econtacts[contactIndex].Name} onSelect={(key:string) => {
      let index = econtacts.findIndex((condet: any) => condet.Name === key);
      setContactIndex(index);

    }

    }>{
      econtacts.map((contactdetails: any, index: number) => (
          <Option key={index} data={contactdetails} value={contactdetails.Name}> {contactdetails.Name} </Option>
        ))
      }
    </Select>
  );

  const emergencycontact =  array(econtacts).map((a: any, index: any) => (
    ec(
        a
    )
  ));

  const emailaddress = array(patientvalue.Addresses).filter((a: any) => (
    a.Type === 'Permanent'
  )).map((a: any) => (
    address(
        a,
    )
  ));


  let realUI = (
    <div></div>
  );

  if((patientdetails && patientdetails.length>0) && patientvalue){
    realUI = (
    <Row gutter={12}>
     <Col span={8}>
        <Icon type="book" style={{ fontSize: "16px", color: "hotpink" }}/>
        {" "}<Text strong>PCP Info</Text>
        <br/><br/>
        <NV name='PCP Name' value={pcpdata && pcpdata[0] && pcpdata[0].PhysicianName} />
        <NV name='PCP Center' value={pcpdata && pcpdata[0] && pcpdata[0].pcpcenter} />
        <NV name='PCP Dept' value={pcpdata && pcpdata[0] && pcpdata[0].Speciality} />
     </Col>
    <Col span={2}>
        <Divider type="vertical" style={{ height: "190px" }} />
    </Col>

    <Col span={7}>
    <Icon type="info-circle" style={{ fontSize: "16px", color: "hotpink" }} />
    {" "}<Text strong>Extra Info</Text>
    <br/>
    <NV name='Emergency Contact Name'/> {backspace}
    <NV name='Contact Relation' value={emergencycontact && emergencycontact[contactIndex] && emergencycontact[contactIndex].relation} />
    <NV name='Contact Num' value={emergencycontact && emergencycontact[contactIndex] && emergencycontact[contactIndex].phones && emergencycontact[contactIndex].phones[0] && emergencycontact[contactIndex].phones[0].num} />
    </Col>
    <Col span={5}>
    <br/><br/>
    <NV name='Email' value={(emailaddress && emailaddress[0])?(emailaddress && emailaddress[0].email): ''} />
    <NV name='Race' value={patientvalue.Race} />
    </Col>
    </Row>
    );

  }

  return (
    <>
    {realUI}
    </>
  );
};
