import * as React from "react";
import {useState} from "react";
import Icon from "antd/lib/icon";
import Table from "antd/lib/table";
import Dropdown from "antd/lib/dropdown";
import Button from "antd/lib/button";
import Menu from "antd/lib/menu";
import Modal from "antd/lib/modal";
import Row from "antd/lib/row";
import Typography from "antd/lib/typography";
import Col from "antd/lib/col";
import { useSelector, useDispatch } from "react-redux";
import {useCtx} from 'one-ui-provider';
import { isArray } from "util";
import Input from 'antd/lib/input'; 
import Popconfirm from 'antd/lib/Popconfirm';
import message from 'antd/lib/message';
import { array } from 'one-core';


const { Text } = Typography;
const { TextArea } = Input;




export const AppointmentTable = () => {

  const { settings,log, agentId } = useCtx();
  const { epic_config = {} } = settings;
  const { location, activityId } = epic_config;
  const  dispatch = useDispatch();
  const { selected: patientvalue = {} } = useSelector((state: any) => (
    state.patient || {}
  ));
  const [moredetmodel, setMoredetmodel] = useState(false);
  const [cancelappmodel, setCancelappmodel] = useState(false);
  const [moredetails, setMoredetails] = useState({});
  const [canceldetails, setCanceldetails] = useState({});


  const { apptscreenpopactive,
    apptscreenpopmsg } = useSelector((state: any) => (
    state.screenpop || {}
   ));

   if(apptscreenpopactive){
    if(apptscreenpopmsg && apptscreenpopmsg === 'Epic Screen pop to the Appointment Desk is successfully initiated'){
        message.success(apptscreenpopmsg);
    }else{
        message.error(apptscreenpopmsg);
    }

    dispatch({
        type:"one-screenpop-reset",
        payload : {
            apptscreenpopactive: false
        }
    })
  } 


  let pdata = useSelector((state:any) => state.appointment);
  let cancelmsg = pdata.cancelmsg;
  if(cancelmsg){
    let apptcode = pdata.apptcode;
    if(apptcode === '1'){
      message.success(cancelmsg);
    }else{
      message.error('Unable to cancel the appointment, please try to cancel the appointment in Epic Hyperspace');
    }
    dispatch({
      type: "reset-appointment-notification",
      payload: {
        cancelmsg: null,
        apptcode: '0'
      }
  });

  }
  let patientdata = pdata.appointments;
  let data: any = [];
  if(patientdata && isArray(patientdata)) {
    let keyindex = 1;
    patientdata.map((ele: any) => {
      let patientId:any = null;
      let patientType:any = null;
      let contactId:any = null;
      let contactType:any = null;
      ele.PatientIDs.map((index: any) => {
        patientId = (index.ID)?index.ID.trim():null;
        patientType = (index.Type)?index.Type.trim():null;
      })
      ele.ContactIDs.map((index: any) => {
        contactId = (index.ID)?index.ID.trim():null;
        contactType = (index.Type)?index.Type.trim():null;
      })
      ele.Providers.map((index: any) => {
        const info = {
          key: keyindex++,
          date: ele.Date,
          appoit: ele.AppointmentStartTime,
          visit: ele.VisitTypeName,
          dptname : index.DepartmentName,
          Providername:index.ProviderName,
          patientid: patientId,
          patienttype: patientType,
          contactid: contactId,
          contacttype: contactType
        }
        data.push(info);
      })
    })
  }

  function sortFunction(a: any, b: any) {
    var dateA = new Date(a.date).getTime();
    var dateB = new Date(b.date).getTime();
    return dateA > dateB ? 1 : -1;
    };
    data.sort(sortFunction)

//   data = sdata.schedule;
  const { resolver } = useCtx();

  const handleApptScreenPopClick = () => {
    let pidepi = array(patientvalue.PatientID).find((id: any) => (
      id['Type'] === "EPI"
    ));
    let patientid = pidepi && pidepi.ID;
    if(patientid){
       dispatch({
            type: 'one-appointment-screenpop-get',
            payload:{
                agentId: agentId,
                location: location,
                activityId: 'ES_APPT_DESK_WEB',
                patientid: patientid
            }
       });
    }else{
      message.error('Epic Screenpop failed. Please make sure you are connected to the Snap Adapter');
    }
  }

  const handleMenuClick = (e:any) => {
    let key = e.key;
    var res = key.split(",");
    if(res[1] === '1'){
      setMoredetmodel(true);
      data.map((rec:any,index:any) => {
        if(res[0] === rec.key.toString()){
          setMoredetails(rec);
        }
      });
    }
    else if(res[1] === '2'){
      setCancelappmodel(true);
      data.map((rec:any,index:any) => {
        if(res[0] === rec.key.toString()){
          setCanceldetails(rec);
        }
      });
    }
    else if(res[1] === '3'){
      handleApptScreenPopClick();
    }
  }

  const handleOk = (e:any) => {
    setMoredetmodel(false);
  };

  const handleAPPOk = (e:any) => {
    setCancelappmodel(false);
  };

  const handleCancel = (e:any) => {
    setMoredetmodel(false);
  };

  const handleAPPCancel = (e:any) => {
    setCancelappmodel(false);
  };

  const handleDelete = (e: any) => {
      console.log('cancel app::', e);
      const payload = {
        PatientID: e.patientid,
	      //"PatientIDType": e.patienttype,
	      ContactID: e.contactid,
	      //"ContactIDType": e.contacttype,
	      Reason: document.getElementsByClassName("appreason")[0].value,
	      Comment: document.getElementsByClassName("appcomments")[0].value
      }
      console.log(payload);
      dispatch({
          type: 'cancel-appt',
          payload: payload
      })
      handleAPPCancel(null);
  };



  const menu = (e:any) => (
    <Menu onClick={handleMenuClick}>
      <Menu.Item key={e.key+",1"}>More Details</Menu.Item>
      <Menu.Item key={e.key+",2"}>Cancel Appointments</Menu.Item>
      <Menu.Item key={e.key+",3"}>Go To Appointment Desk</Menu.Item>
    </Menu>
  );

  const columns = [
    {
      title: "Date",
      dataIndex: "date",
      key: "date"
    },
    {
      title: "Department",
      dataIndex: "dptname",
      key: "department"
    },
    {
      title: "Appt.Time",
      dataIndex: "appoit",
      key: "apptTime"
    },
    {
      title: "Provider",
      key: "provider",
      dataIndex: "Providername"
    },
    {
      title: "Visit Type",
      key: "visittype",
      dataIndex: "visit"
    },
    {
      title: "Action",
      key: "action",
      render: (text:any, record:any) => (
          <Dropdown overlay={menu(record)}>
            <Button type="primary">
              Actions <Icon type="down" />
            </Button>
          </Dropdown>
      )
    }
  ];


  return (
    <div>
      <Icon type="unordered-list" /> All Appointments
      <Table columns={columns} dataSource={data} />
      <Modal
          title="Appointment Details"
          visible={moredetmodel}
          onOk={handleOk}
          onCancel={handleCancel}
          width={720}
          footer={[
            <Button key="submit" type="primary" onClick={handleOk}>
              CLOSE
            </Button>,
          ]}
         >
          <Row gutter={8} align='middle' style={{padding:"2px", height: "100px"}}>
              <Col span={16}>
                  <Row>
                    <Text>Appointment Date: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.date:""}</Text>
                  </Row>
                  <Row>
                    <Text>Department Name: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.dptname:""}</Text>
                  </Row>
                  <Row>
                    <Text>Appointment Time: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.appoit:""}</Text>
                  </Row>
                  {/* <Row>
                    <Text>Hospital/Clinic Name: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.department:""}</Text>
                  </Row> */}
                  <Row>
                    <Text>Provider: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.Providername:""}</Text>
                  </Row>
                  <Row>
                    <Text>Appointment Visit Type: </Text> {" "} <Text strong>{moredetails!=undefined?moredetails.visit:""}</Text>
                  </Row>
              </Col>
          </Row>
         </Modal>

         <Modal
          title="Cancel Appointment"
          visible={cancelappmodel}
          onCancel={handleAPPCancel}
          width={720}

          footer={[
            <Button key="submit" type="primary" onClick={handleAPPOk}>
              CLOSE
            </Button>,
          ]}
         >
          <Row gutter={8} align='middle' style={{padding:"2px", height: "300px"}}>
              <Col span={10}>
                  <Row>
                    <Text>Reason: </Text> {" "} <TextArea className='appreason' rows={3} cols={2}/>
                    <Text>Comments: </Text> {" "} <TextArea className='appcomments' rows={3} />
                    <br/><br/><br/>
                    <Popconfirm title="Sure to Cancel Appointment?" onConfirm={() => handleDelete(canceldetails)}>
                        <Button type="primary">Cancel Appointment</Button>
                    </Popconfirm>
                  </Row>
              </Col>
              <Col span={2}></Col>
              <Col span={10}>
                   <Text>Date:</Text> {" "} {canceldetails.date}
                   <br/>
                   <Text>Department:</Text> {" "} {canceldetails.dptname}
                   <br/>
                   <Text>Appointment Time:</Text> {" "} {canceldetails.dptname}
                   <br/>
                   <Text>Provider:</Text> {" "} {canceldetails.dptname}
                   <br/>
                   <Text>Purpose:</Text> {" "} {canceldetails.visit}
              </Col>
          </Row>
         </Modal>
    </div>
  );
};
