import { Event } from 'one-interact';
import { VoiceEvent } from 'one-interact-voice';

import { Action } from './action'
import { Actions } from './actions'

const initial: Event = {
    type: VoiceEvent.END,
};

export const reducer = (context: any) => {
    const { log } = context;
    return (state: Event = initial, action: Action<Actions>) => {
        log.debug(
            'one-state-interact-voice',
            'reducer', {
                action,
            }
        );

        let updated = state;
        const { type, payload } = action;
        switch (type) {
            case Actions.SET:
                updated = {
                    ...payload,
                };
                break;

            case Actions.RESET:
                updated = initial;
                break;
        }
        return updated;
    }
}
